import React from 'react';
import { HashRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { Home, Menu, X, Mail, Phone, MapPin, Facebook, Linkedin, Instagram, ChevronDown, Camera, Clock } from 'lucide-react';

// --- OFFICIAL TikTok SVG ---
const TikTokIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12.53 2.12c-.52.28-.79.88-.66 1.48.13.59.7.97 1.3.84 3.23-.7 6.64 1.15 7.37 4.79.4 1.94-.13 3.86-1.55 5.23-1.43 1.38-3.4 2.05-5.45 1.76l-.16.03c-.63.12-1.28.02-1.89-.3.51.13 1.05.18 1.57.14 2.05-.14 3.96-.86 5.39-2.23 1.42-1.38 1.95-3.3 1.55-5.23-.72-3.64-4.14-5.49-7.37-4.79ZM5.5 13.5v5c0 1.93 1.57 3.5 3.5 3.5h7c1.93 0 3.5-1.57 3.5-3.5v-5c0-.55-.45-1-1-1s-1 .45-1 1v5c0 .83-.67 1.5-1.5 1.5h-7c-.83 0-1.5-.67-1.5-1.5v-5c0-.55-.45-1-1-1s-1 .45-1 1ZM10 13c.55 0 1-.45 1-1v-4c0-.55-.45-1-1-1s-1 .45-1 1v4c0 .55.45 1 1 1ZM15 13c.55 0 1-.45 1-1v-4c0-.55-.45-1-1-1s-1 .45-1 1v4c0 .55.45 1 1 1Z"/>
  </svg>
);

// --- Config ---
const CONTACT_EMAIL = 'sales@senrans.com';
const WHATSAPP_NUMBER = '+254725723091';
const WHATSAPP_LINK = `https://wa.me/${WHATSAPP_NUMBER.replace(/\+/g, '')}`;
const COMPANY_ADDRESS = 'Repen Complex, Block B, 3rd Floor, office 310B, Nairobi, Kenya';
const MAP_EMBED = `https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3988.789!2d36.928722!3d-1.378278!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A00!2zMcKwMjInNDEuOCJTIDM2wrA1NSc0My40IkU!5e1!3m2!1sen!2ske!4v1234567890`;
const TIKTOK_LINK = 'https://www.tiktok.com/@senrans_ltd';

// --- Nav Item ---
const NavItem = ({ to, children, isDropdown = false, onToggle }) => {
  const location = useLocation();
  const isActive = location.pathname === to || (to !== '/' && location.pathname.startsWith(to));
  return (
    <li className="relative group">
      <Link
        to={to}
        onClick={isDropdown ? onToggle : undefined}
        className={`flex items-center p-2 text-white font-semibold hover:text-red-400 transition ${isActive ? 'border-b-2 border-white' : ''}`}
      >
        {children}
        {isDropdown && <ChevronDown size={14} className="ml-1 transition-transform group-hover:rotate-180" />}
      </Link>
    </li>
  );
};

// --- Header ---
const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);
  const [isProductsOpen, setIsProductsOpen] = React.useState(false);
  const closeMenu = () => { setIsMenuOpen(false); setIsProductsOpen(false); };

  return (
    <header className="bg-black shadow-lg sticky top-0 z-50 text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <Link to="/" className="flex items-center">
            <img src="/logo.png" alt="Senrans Ltd" className="h-12 w-auto" />
          </Link>

          {/* Desktop */}
          <nav className="hidden md:block">
            <ul className="flex space-x-4">
              <NavItem to="/about">About Us</NavItem>
              <NavItem to="/products-services">Products & Services</NavItem>
              <NavItem to="/gallery">Gallery</NavItem>
              <NavItem to="/contact">Contact Us</NavItem>
            </ul>
          </nav>

          {/* Mobile */}
          <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="md:hidden text-white">
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="md:hidden bg-red-800">
          <div className="px-2 pt-2 pb-3 space-y-1">
            <Link to="/about" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">About Us</Link>
            <Link to="/products-services" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Products & Services</Link>
            <Link to="/gallery" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Gallery</Link>
            <Link to="/contact" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Contact Us</Link>
          </div>
        </div>
      )}
    </header>
  );
};

// --- PARTNERS CAROUSEL ---
const PartnersCarousel = () => {
  const partners = [
    { name: "Siemens", logo: "/partners/siemens.png" },
    { name: "Schneider", logo: "/partners/schneider.png" },
    { name: "ABB", logo: "/partners/abb.png" },
    { name: "Eaton", logo: "/partners/eaton.png" },
    { name: "Danfoss", logo: "/partners/danfoss.png" },
    { name: "Deep Sea Electronics", logo: "/partners/deepsea.png" },
    { name: "Himel", logo: "/partners/himel.png" },
    { name: "Siemens", logo: "/partners/siemens.png" },
    { name: "Schneider", logo: "/partners/schneider.png" },
  ];

  return (
    <section className="bg-white py-12 overflow-hidden">
      <div className="max-w-7xl mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-10 text-gray-800">
          PARTNERS WE WORK WITH
        </h2>
        <div className="relative">
          <div className="flex animate-scroll gap-8 items-center">
            {partners.map((p, i) => (
              <div key={i} className="flex-shrink-0 w-32 h-20 md:w-40 md:h-24 bg-gray-50 rounded-lg flex items-center justify-center p-4 shadow-sm">
                <img src={p.logo} alt={p.name} className="max-h-full max-w-full object-contain" />
              </div>
            ))}
          </div>
        </div>
      </div>
      <style jsx>{`
        @keyframes scroll {
          0% { transform: translateX(0); }
          100% { transform: translateX(-50%); }
        }
        .animate-scroll {
          display: flex;
          width: max-content;
          animation: scroll 25s linear infinite;
        }
        .animate-scroll:hover {
          animation-play-state: paused;
        }
      `}</style>
    </section>
  );
};

// --- Home ---
const HomeContent = () => {
  const [current, setCurrent] = React.useState(0);
  const slides = [
    { img: "/images/slide1.jpg", title: "Custom LV Panels", text: "Built to your exact specifications" },
    { img: "/images/slide2.jpg", title: "Motor Control Centers", text: "Automation & precision control" },
    { img: "/images/slide3.jpg", title: "Power Distribution", text: "Safe, reliable, scalable" },
  ];

  React.useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <div>
      <div className="relative h-96 md:h-screen overflow-hidden">
        {slides.map((s, i) => (
          <div key={i} className={`absolute inset-0 transition-opacity duration-1000 ${i === current ? 'opacity-100' : 'opacity-0'}`}>
            <img src={s.img} alt={s.title} className="w-full h-full object-cover" />
            <div className="absolute inset-0 bg-black bg-opacity-60 flex items-center justify-center text-center p-6">
              <div>
                <h1 className="text-3xl md:text-6xl font-bold text-white mb-4">{s.title}</h1>
                <p className="text-lg md:text-2xl text-red-300">{s.text}</p>
              </div>
            </div>
          </div>
        ))}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
          {slides.map((_, i) => (
            <button key={i} onClick={() => setCurrent(i)} className={`w-3 h-3 rounded-full transition ${i === current ? 'bg-white' : 'bg-gray-500'}`} />
          ))}
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-12">
        <PartnersCarousel />
        <section className="bg-red-600 text-white p-10 rounded-xl text-center">
          <h3 className="text-2xl font-bold mb-4">Need Custom LV Panels or Installation?</h3>
          <a href={WHATSAPP_LINK} className="bg-white text-red-600 px-8 py-3 rounded-full font-bold inline-flex items-center hover:bg-gray-100">
            Get Quote <Phone size={20} className="ml-2" />
          </a>
        </section>
      </div>
    </div>
  );
};

// --- About Us ---
const About = () => {
  const collageImages = Array(6).fill().map((_, i) => `/images/collage${i + 1}.jpg`);

  return (
    <div className="max-w-7xl mx-auto px-4 py-12">
      <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">About Senrans Ventures Ltd</h1>

      <div className="bg-white p-8 rounded-xl shadow-lg text-lg leading-relaxed mb-12">
        <p className="mb-6">
          <strong>Senrans Ventures Ltd is your trusted partner in Low Voltage (LV) electrical engineering.</strong> We specialize in the design, supply, installation, testing, and commissioning of custom LV panels and control systems.
        </p>
        <p className="mb-6">
          From <strong>Main Distribution Boards</strong> to <strong>Motor Control Centers (MCCs)</strong>, <strong>Power Factor Correction Units</strong>, and <strong>Automation Panels</strong>  we build solutions tailored to your exact needs.
        </p>
        <p className="mb-6">
          Our services include <strong>24/7 maintenance</strong>: predictive, preventative, and corrective. We offer thermography, fault diagnosis, safe cleaning, and operational checks to keep your systems running smoothly.
        </p>
        <p className="mb-6 italic text-gray-700">
          We also undertake select MV/HV projects (11kV33kV) on a case-by-case basis.
        </p>

        <div className="grid md:grid-cols-2 gap-8 mt-10">
          <div className="bg-gray-50 p-6 rounded-lg">
            <h3 className="text-xl font-bold text-red-600 mb-3">OUR VISION</h3>
            <p>To be the leading LV electrical engineering firm in East Africa.</p>
          </div>
          <div className="bg-gray-50 p-6 rounded-lg">
            <h3 className="text-xl font-bold text-red-600 mb-3">Our Mission</h3>
            <p>To deliver high-quality, safe, and efficient LV solutions through custom design and expert installation.</p>
          </div>
        </div>

        <div className="mt-10">
          <h3 className="text-xl font-bold text-red-600 mb-4">Our Core Values</h3>
          <ul className="space-y-2 text-gray-700">
            <li className="flex items-start"><span className="text-red-600 mr-2"></span> Quality & Safety First</li>
            <li className="flex items-start"><span className="text-red-600 mr-2"></span> Precision in Every Panel</li>
