import React from 'react';
import { HashRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { Menu, X, Mail, Phone, MapPin, Facebook, Linkedin, Instagram, Clock, ArrowRight, Zap, Wrench, Sun, Shield, Activity, Settings, Search, Thermometer, Calendar } from 'lucide-react';

const TikTokIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12.53 2.12c-.52.28-.79.88-.66 1.48.13.59.7.97 1.3.84 3.23-.7 6.64 1.15 7.37 4.79.4 1.94-.13 3.86-1.55 5.23-1.43 1.38-3.4 2.05-5.45 1.76l-.16.03c-.63.12-1.28.02-1.89-.3.51.13 1.05.18 1.57.14 2.05-.14 3.96-.86 5.39-2.23 1.42-1.38 1.95-3.3 1.55-5.23-.72-3.64-4.14-5.49-7.37-4.79ZM5.5 13.5v5c0 1.93 1.57 3.5 3.5 3.5h7c1.93 0 3.5-1.57 3.5-3.5v-5c0-.55-.45-1-1-1s-1 .45-1 1v5c0 .83-.67 1.5-1.5 1.5h-7c-.83 0-1.5-.67-1.5-1.5v-5c0-.55-.45-1-1-1s-1 .45-1 1ZM10 13c.55 0 1-.45 1-1v-4c0-.55-.45-1-1-1s-1 .45-1 1v4c0 .55.45 1 1 1ZM15 13c.55 0 1-.45 1-1v-4c0-.55-.45-1-1-1s-1 .45-1 1v4c0 .55.45 1 1 1Z"/>
  </svg>
);

const CONTACT_EMAIL = 'sales@senrans.com';
const WHATSAPP_NUMBER = '+254725723091';
const WHATSAPP_LINK = `https://wa.me/${WHATSAPP_NUMBER.replace(/\+/g, '')}`;
const COMPANY_ADDRESS = 'Repen Complex, Block B, 3rd Floor, office 310B, Nairobi, Kenya';
const MAP_EMBED = `https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3988.789!2d36.928722!3d-1.378278!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A00!2zMcKwMjInNDEuOCJTIDM2wrA1NSc0My40IkU!5e1!3m2!1sen!2ske!4v1234567890`;
const TIKTOK_LINK = 'https://www.tiktok.com/@senrans_ltd';

const SOCIAL_LINKS = {
  facebook: 'https://www.facebook.com/people/Senrans-Ventures/pfbid0XufECfqUEoAKRqbuHT2vkn8EdSvHQxFX8HueCJYLHYb2ajG19v8ntsadvQpXn9nl/',
  instagram: 'https://www.instagram.com/senrans_ltd_/?igsh=MTk5OWJ2NnRudWoxag%3D%3D',
  linkedin: '#',
  tiktok: TIKTOK_LINK
};

const SERVICES = [
  { icon: Zap, title: "Electrical Installations", desc: "Complete wiring & lighting systems" },
  { icon: Wrench, title: "Panel Assembly", desc: "Custom LV panels built in-house" },
  { icon: Activity, title: "Energy Monitoring", desc: "Real-time power analytics" },
  { icon: Sun, title: "Solar Systems", desc: "Hybrid & off-grid solutions" },
  { icon: Shield, title: "Backup Products", desc: "UPS, inverters & generators" },
  { icon: Settings, title: "DSE Controllers", desc: "Auto-transfer & sync panels" },
  { icon: Zap, title: "Power Factor Correction", desc: "Save 30% on KPLC bills" },
  { icon: Calendar, title: "Annual Maintenance", desc: "24/7 service contracts" },
  { icon: Search, title: "Electrical Inspections", desc: "Compliance & safety audits" },
  { icon: Thermometer, title: "Thermography", desc: "Prevent downtime with heat scans" }
];

const NavItem = ({ to, children }) => {
  const location = useLocation();
  const isActive = location.pathname === to || (to !== '/' && location.pathname.startsWith(to));
  return (
    <li>
      <Link to={to} className={`block px-3 py-2 text-white font-bold hover:text-red-400 transition ${isActive ? 'border-b-2 border-white' : ''}`}>
        {children}
      </Link>
    </li>
  );
};

const Header = () => {
  const [open, setOpen] = React.useState(false);
  return (
    <header className="bg-black sticky top-0 z-50 shadow-2xl">
      <div className="max-w-7xl mx-auto px-4 flex justify-between items-center h-16">
        <Link to="/"><img src="/logo.png" alt="Senrans" className="h-12" /></Link>
        <nav className="hidden md:flex gap-8">
          <NavItem to="/about">About</NavItem>
          <NavItem to="/products-services">Products</NavItem>
          <NavItem to="/gallery">Gallery</NavItem>
          <NavItem to="/contact">Contact</NavItem>
        </nav>
        <button onClick={() => setOpen(!open)} className="md:hidden">
          {open ? <X size={28} /> : <Menu size={28} />}
        </button>
      </div>
      {open && (
        <div className="md:hidden bg-red-800 p-4">
          <NavItem to="/about">About</NavItem>
          <NavItem to="/products-services">Products</NavItem>
          <NavItem to="/gallery">Gallery</NavItem>
          <NavItem to="/contact">Contact</NavItem>
        </div>
      )}
    </header>
  );
};

const ServicePanel = ({ service, index }) => {
  const Icon = service.icon;
  return (
    <div className={`
      group relative bg-white rounded-3xl overflow-hidden shadow-xl
      hover:shadow-3xl hover:-translate-y-4 transition-all duration-500
      border border-gray-100
    `}>
      <div className="absolute inset-0 bg-gradient-to-br from-red-50 to-transparent opacity-0 group-hover:opacity-100 transition" />
      <div className="p-10 md:p-12 text-center relative z-10">
        <div className="mb-6 inline-block p-6 bg-red-100 rounded-full group-hover:bg-red-600 transition">
          <Icon size={48} className="text-red-600 group-hover:text-white transition" />
        </div>
        <h3 className="text-2xl md:text-3xl font-bold text-gray-800 mb-3">
          {service.title}
        </h3>
        <p className="text-gray-600 text-lg leading-relaxed">
          {service.desc}
        </p>
        <div className="mt-8 opacity-0 group-hover:opacity-100 transition">
          <a href={WHATSAPP_LINK} className="inline-flex items-center gap-2 text-red-600 font-bold hover:gap-4 transition-all">
            Get Quote <ArrowRight size={20} />
          </a>
        </div>
      </div>
      <div className="h-2 bg-gradient-to-r from-red-500 to-red-700 scale-x-0 group-hover:scale-x-100 transition-transform origin-left" />
    </div>
  );
};

const HomeContent = () => {
  return (
    <>
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        .float { animation: float 6s ease-in-out infinite; }
        .float-delay-1 { animation-delay: -2s; }
        .float-delay-2 { animation-delay: -4s; }
      `}</style>

      <section className="py-20 px-4 bg-gradient-to-b from-gray-50 to-white">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-16">
            <h1 className="text-5xl md:text-7xl font-black text-gray-900 mb-6">
              POWERING <span className="text-red-600">EAST AFRICA</span>
            </h1>
            <p className="text-xl md:text-2xl text-gray-600 max-w-4xl mx-auto">
              End-to-end electrical engineering: Design • Build • Maintain • 24/7
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-10">
            {SERVICES.map((service, i) => (
              <div key={i} className={`float ${i % 3 === 1 ? 'float-delay-1' : i % 3 === 2 ? 'float-delay-2' : ''}`}>
                <ServicePanel service={service} index={i} />
              </div>
            ))}
          </div>

          <div className="mt-20 text-center">
            <div className="inline-block bg-gradient-to-r from-red-600 to-red-700 text-white p-12 rounded-3xl shadow-2xl">
              <h2 className="text-4xl font-bold mb-4">Ready to Power Up?</h2>
              <p className="text-xl mb-8 opacity-90">LV Panels • ATS • Solar • Maintenance</p>
              <a href={WHATSAPP_LINK} className="inline-flex items-center gap-4 bg-white text-red-600 px-12 py-5 rounded-full font-bold text-xl hover:scale-105 transition shadow-xl">
                <Phone size={28} /> GET INSTANT QUOTE
              </a>
            </div>
          </div>
        </div>
      </section>

      <section className="bg-black py-16">
        <div className="max-w-7xl mx-auto px-4">
          <h2 className="text-4xl font-bold text-white text-center mb-12">TRUSTED BY INDUSTRY LEADERS</h2>
          <div className="grid grid-cols-3 md:grid-cols-7 gap-8 opacity-80">
            {['siemens', 'schneider', 'abb', 'eaton', 'danfoss', 'deepsea', 'himel'].map((p) => (
              <img key={p} src={`/partners/${p}.png`} alt={p} className="h-16 mx-auto filter brightness-0 invert" />
            ))}
          </div>
        </div>
      </section>
    </>
  );
};

// === ALL OTHER PAGES (About, Products, Gallery, Contact, Footer) ===
// (100% unchanged — just paste your old ones below)

const About = () => { /* YOUR ORIGINAL ABOUT CODE */ };
const ProductsList = () => { /* YOUR ORIGINAL PRODUCTS LIST */ };
const ProductDetail = ({ title, children, img1, img2 }) => { /* YOUR ORIGINAL */ };
const MDB = () => { /* YOUR ORIGINAL */ };
const MCC = () => { /* YOUR ORIGINAL */ };
const PFC = () => { /* YOUR ORIGINAL */ };
const ATS = () => { /* YOUR ORIGINAL */ };
const Breaker = () => { /* YOUR ORIGINAL */ };
const Sensor = () => { /* YOUR ORIGINAL */ };
const Gallery = () => { /* YOUR ORIGINAL */ };
const Contact = () => { /* YOUR ORIGINAL */ };

const Footer = () => (
  <footer className="bg-black text-white py-16">
    <div className="max-w-7xl mx-auto px-4">
      <div className="grid md:grid-cols-4 gap-10 border-b border-gray-800 pb-12">
        <div>
          <img src="/logo.png" alt="Senrans" className="h-12 mb-4" />
          <p className="text-gray-400 text-sm">LV Panel Experts • DSE ATS • Solar • 24/7 Service</p>
        </div>
        <div>
          <h3 className="font-bold text-lg mb-4 border-b border-red-600 pb-2 inline-block">Quick Links</h3>
          <ul className="space-y-2 text-sm"><li><Link to="/" className="hover:text-red-400">Home</Link></li><li><Link to="/about" className="hover:text-red-400">About</Link></li><li><Link to="/products-services" className="hover:text-red-400">Products</Link></li></ul>
        </div>
        <div>
          <h3 className="font-bold text-lg mb-4 border-b border-red-600 pb-2 inline-block">Contact</h3>
          <p className="text-sm space-y-2">
            <span className="flex items-center gap-2"><Mail size={16} /> {CONTACT_EMAIL}</span>
            <span className="flex items-center gap-2"><Phone size={16} /> {WHATSAPP_NUMBER}</span>
          </p>
        </div>
        <div className="flex gap-4">
          <a href={SOCIAL_LINKS.facebook} className="text-gray-400 hover:text-white"><Facebook size={24} /></a>
          <a href={SOCIAL_LINKS.instagram} className="text-gray-400 hover:text-white"><Instagram size={24} /></a>
          <a href={SOCIAL_LINKS.tiktok} className="text-gray-400 hover:text-white"><TikTokIcon /></a>
        </div>
      </div>
      <p className="text-center text-gray-500 text-sm pt-8">© 2025 Senrans Ventures Ltd. All Rights Reserved.</p>
    </div>
  </footer>
);

const ScrollToTop = () => {
  const { pathname } = useLocation();
  React.useEffect(() => window.scrollTo(0, 0), [pathname]);
  return null;
};

const App = () => (
  <Router>
    <ScrollToTop />
    <div className="min-h-screen bg-gray-50">
      <Header />
      <main>
        <Routes>
          <Route path="/" element={<HomeContent />} />
          <Route path="/about" element={<About />} />
          <Route path="/products-services" element={<ProductsList />} />
          <Route path="/products-services/mdb" element={<MDB />} />
          <Route path="/products-services/mcc" element={<MCC />} />
          <Route path="/products-services/pfc" element={<PFC />} />
          <Route path="/products-services/ats" element={<ATS />} />
          <Route path="/products-services/breaker" element={<Breaker />} />
          <Route path="/products-services/sensor" element={<Sensor />} />
          <Route path="/gallery" element={<Gallery />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </main>
      <Footer />
      <a href={WHATSAPP_LINK} className="fixed bottom-6 right-6 bg-green-500 p-5 rounded-full shadow-2xl z-50 hover:scale-110 transition">
        <Phone size={32} className="text-white" />
      </a>
    </div>
  </Router>
);

export default App;
