import React from 'react';
import { HashRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { Home, Menu, X, Mail, Phone, MapPin, Facebook, Linkedin, Instagram, ChevronDown, CheckCircle, Camera, Sun } from 'lucide-react';

// --- Config ---
const CONTACT_EMAIL = 'sales@senrans.com';
const WHATSAPP_NUMBER = '+254725723091';
const WHATSAPP_LINK = `https://wa.me/${WHATSAPP_NUMBER.replace(/\+/g, '')}`;
const COMPANY_ADDRESS = 'Repen Complex, Block B, 3rd Floor, office 310B, Nairobi, Kenya';

// --- Nav Item ---
const NavItem = ({ to, children, isDropdown = false, onToggle }) => {
  const location = useLocation();
  const isActive = location.pathname === to || (to !== '/' && location.pathname.startsWith(to));
  return (
    <li className="relative group">
      <Link
        to={to}
        onClick={isDropdown ? onToggle : undefined}
        className={`flex items-center p-2 text-white font-semibold hover:text-red-300 transition ${isActive ? 'border-b-2 border-white' : ''}`}
      >
        {children}
        {isDropdown && <ChevronDown size={14} className="ml-1 transition-transform group-hover:rotate-180" />}
      </Link>
    </li>
  );
};

// --- Header ---
const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);
  const [isProductsOpen, setIsProductsOpen] = React.useState(false);
  const closeMenu = () => { setIsMenuOpen(false); setIsProductsOpen(false); };

  return (
    <header className="bg-black shadow-lg sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <Link to="/" className="text-white text-xl font-extrabold flex items-center">
            <div className="w-8 h-8 bg-red-600 rounded-full flex items-center justify-center mr-2">
              <span className="text-white text-sm font-black">SL</span>
            </div>
            SENRANS LTD
          </Link>

          {/* Desktop */}
          <nav className="hidden md:block">
            <ul className="flex space-x-4">
              <NavItem to="/">Home</NavItem>
              <NavItem to="/about">About Us</NavItem>
              <li className="relative group" onMouseLeave={() => setIsProductsOpen(false)}>
                <a href="#" onMouseEnter={() => setIsProductsOpen(true)} onClick={e => e.preventDefault()}
                   className="flex items-center p-2 text-white font-semibold hover:text-red-300">
                  Products & Solutions <ChevronDown size={14} className="ml-1 transition-transform group-hover:rotate-180" />
                </a>
                {isProductsOpen && (
                  <div className="absolute left-0 mt-0 w-48 bg-red-700 rounded-md shadow-lg py-1 z-50">
                    <Link to="/products-services" onClick={closeMenu} className="block px-4 py-2 text-sm text-white hover:bg-red-600">Overview</Link>
                    <Link to="/products-services/lv" onClick={closeMenu} className="block px-4 py-2 text-sm text-white hover:bg-red-600">Low Voltage</Link>
                    <Link to="/products-services/mvhv" onClick={closeMenu} className="block px-4 py-2 text-sm text-white hover:bg-red-600">MV & HV</Link>
                    <Link to="/products-services/solar" onClick={closeMenu} className="block px-4 py-2 text-sm text-white hover:bg-red-600">Solar PV</Link>
                  </div>
                )}
              </li>
              <NavItem to="/gallery">Gallery</NavItem>
              <NavItem to="/contact">Contact Us</NavItem>
            </ul>
          </nav>

          {/* Mobile */}
          <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="md:hidden text-white">
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="md:hidden bg-red-800">
          <div className="px-2 pt-2 pb-3 space-y-1">
            <Link to="/" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Home</Link>
            <Link to="/about" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">About Us</Link>
            <button onClick={() => setIsProductsOpen(!isProductsOpen)} className="w-full text-left flex justify-between px-3 py-2 text-white hover:bg-red-700">
              Products & Solutions <ChevronDown size={16} className={isProductsOpen ? 'rotate-180' : ''} />
            </button>
            {isProductsOpen && (
              <div className="pl-6 space-y-1">
                <Link to="/products-services" onClick={closeMenu} className="block px-3 py-2 text-sm text-white hover:bg-red-600">Overview</Link>
                <Link to="/products-services/lv" onClick={closeMenu} className="block px-3 py-2 text-sm text-white hover:bg-red-600">Low Voltage</Link>
                <Link to="/products-services/mvhv" onClick={closeMenu} className="block px-3 py-2 text-sm text-white hover:bg-red-600">MV & HV</Link>
                <Link to="/products-services/solar" onClick={closeMenu} className="block px-3 py-2 text-sm text-white hover:bg-red-600">Solar PV</Link>
              </div>
            )}
            <Link to="/gallery" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Gallery</Link>
            <Link to="/contact" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Contact Us</Link>
          </div>
        </div>
      )}
    </header>
  );
};

// --- Footer ---
const Footer = () => (
  <footer className="bg-black text-white mt-12">
    <div className="max-w-7xl mx-auto px-4 py-10">
      <div className="grid grid-cols-1 md:grid-cols-4 gap-8 border-b border-gray-800 pb-8">
        <div>
          <div className="flex items-center mb-4">
            <div className="w-10 h-10 bg-red-600 rounded-full flex items-center justify-center mr-2">
              <span className="text-white font-black">SL</span>
            </div>
            <h3 className="text-lg font-bold">Senrans Ltd</h3>
          </div>
          <p className="text-sm text-gray-400">Expert electrical engineering in LV, MV, HV & Solar.</p>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-red-600 pb-1">Links</h3>
          <ul className="text-sm space-y-1">
            <li><Link to="/about" className="hover:text-red-400">About</Link></li>
            <li><Link to="/products-services" className="hover:text-red-400">Services</Link></li>
            <li><Link to="/gallery" className="hover:text-red-400">Gallery</Link></li>
          </ul>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-red-600 pb-1">Services</h3>
          <ul className="text-sm space-y-1">
            <li><Link to="/products-services/lv" className="hover:text-red-400">Low Voltage</Link></li>
            <li><Link to="/products-services/mvhv" className="hover:text-red-400">MV & HV</Link></li>
            <li><Link to="/products-services/solar" className="hover:text-red-400">Solar PV</Link></li>
          </ul>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-red-600 pb-1">Contact</h3>
          <p className="flex items-start text-sm mb-2"><MapPin size={16} className="mr-2 mt-1" /> {COMPANY_ADDRESS}</p>
          <p className="flex items-center text-sm mb-2"><Mail size={16} className="mr-2" /> <a href={`mailto:${CONTACT_EMAIL}`} className="hover:text-red-400">{CONTACT_EMAIL}</a></p>
          <p className="flex items-center text-sm"><Phone size={16} className="mr-2" /> <a href={WHATSAPP_LINK} className="hover:text-red-400">{WHATSAPP_NUMBER}</a></p>
          <div className="flex space-x-3 mt-4">
            <a href="#" className="text-gray-400 hover:text-white"><Facebook size={20} /></a>
            <a href="#" className="text-gray-400 hover:text-white"><Linkedin size={20} /></a>
            <a href="#" className="text-gray-400 hover:text-white"><Instagram size={20} /></a>
          </div>
        </div>
      </div>
      <p className="text-center text-sm text-gray-500 pt-6"> {new Date().getFullYear()} Senrans Ltd</p>
    </div>
  </footer>
);

// --- Home ---
const HomeContent = () => {
  const slides = [
    { img: "https://placehold.co/1600x600?text=33kV+Substation", title: "High Voltage Engineering", text: "Substation design & commissioning" },
    { img: "https://placehold.co/1600x600?text=Solar+PV+Farm", title: "Sustainable Solar Solutions", text: "Grid-tied & off-grid systems" },
    { img: "https://placehold.co/1600x600?text=LV+Control+Panel", title: "Smart LV Systems", text: "Automation & control panels" },
  ];
  const partners = ['Siemens', 'Schneider', 'ABB', 'Eaton', 'Danfoss', 'Caterpillar'];

  return (
    <div>
      {/* Carousel */}
      <div className="relative h-96 md:h-screen overflow-hidden">
        {slides.map((s, i) => (
          <div key={i} className={`absolute inset-0 ${i === 0 ? 'opacity-100' : 'opacity-0'} transition-opacity duration-1000`} style={{ animation: `fade 12s infinite`, animationDelay: `${i * 4}s` }}>
            <img src={s.img} alt={s.title} className="w-full h-full object-cover" />
            <div className="absolute inset-0 bg-black bg-opacity-60 flex items-center justify-center text-center p-6">
              <div>
                <h1 className="text-3xl md:text-6xl font-bold text-white mb-4">{s.title}</h1>
                <p className="text-lg md:text-2xl text-red-300">{s.text}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="max-w-7xl mx-auto px-4 py-12">
        <section className="bg-white p-8 rounded-xl shadow mb-12">
          <h2 className="text-3xl font-bold text-center mb-8">Trusted Partners</h2>
          <div className="grid grid-cols-2 md:grid-cols-6 gap-6">
            {partners.map(p => (
              <div key={p} className="bg-gray-100 h-20 rounded-lg flex items-center justify-center text-sm font-semibold text-gray-700">
                {p}
              </div>
            ))}
          </div>
        </section>

        <section className="bg-red-600 text-white p-10 rounded-xl text-center">
          <h3 className="text-2xl font-bold mb-4">Ready to Power Your Project?</h3>
          <a href={WHATSAPP_LINK} className="bg-white text-red-600 px-8 py-3 rounded-full font-bold inline-flex items-center hover:bg-gray-100">
            Get Quote <Phone size={20} className="ml-2" />
          </a>
        </section>
      </div>
    </div>
  );
};

// --- About ---
const About = () => (
  <div className="max-w-7xl mx-auto px-4 py-12">
    <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">About Senrans Ltd</h1>
    <div className="bg-white p-8 rounded-xl shadow-lg text-lg">
      <p className="mb-6">Senrans Ltd is a leading electrical engineering company in Nairobi, Kenya, specializing in <strong>Low Voltage (LV)</strong>, <strong>Medium & High Voltage (MV/HV)</strong>, and <strong>Solar PV</strong> systems.</p>
      <p className="mb-6">With over 10 years of experience, we deliver safe, efficient, and innovative power solutions for industrial, commercial, and utility clients across East Africa.</p>
      <p className="italic text-gray-700">"We engineer reliability, sustainability, and progress."</p>
    </div>
  </div>
);

// --- Products ---
const Products = () => (
  <div className="max-w-7xl mx-auto px-4 py-12">
    <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">Products & Solutions</h1>
    <div className="grid md:grid-cols-3 gap-8">
      {[
        { title: "Low Voltage (LV)", icon: "zap", desc: "Panels, cabling, automation", to: "/products-services/lv" },
        { title: "MV & HV Systems", icon: "zap", desc: "Substations, switchgear", to: "/products-services/mvhv" },
        { title: "Solar PV Solutions", icon: "sun", desc: "Grid-tied & off-grid", to: "/products-services/solar" },
      ].map((item, i) => (
        <Link to={item.to} key={i} className="bg-white p-6 rounded-xl shadow hover:shadow-xl transition text-center">
          <div className="text-5xl mb-4">{item.icon === "sun" ? <Sun className="mx-auto text-yellow-500" size={48} /> : "zap"}</div>
          <h3 className="text-xl font-bold text-red-600 mb-2">{item.title}</h3>
          <p className="text-gray-600">{item.desc}</p>
        </Link>
      ))}
    </div>
  </div>
);

const ProductDetail = ({ title, items }) => (
  <div className="max-w-7xl mx-auto px-4 py-12">
    <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">{title}</h1>
    <ul className="space-y-4">
      {items.map((item, i) => (
        <li key={i} className="bg-white p-6 rounded-xl shadow flex items-center">
          <CheckCircle size={24} className="text-red-600 mr-4" />
          <span className="text-lg">{item}</span>
        </li>
      ))}
    </ul>
  </div>
);

const LV_Products = () => <ProductDetail title="Low Voltage Systems" items={["Distribution Panels", "Motor Control Centers", "Lighting Systems", "Power Factor Correction"]} />;
const MVHV_Products = () => <ProductDetail title="MV & HV Systems" items={["33kV Substations", "Switchgear", "Cable Jointing", "Protection Relays"]} />;
const Solar_Products = () => <ProductDetail title="Solar PV Solutions" items={["Rooftop Solar", "Battery Storage", "Hybrid Systems", "Utility-Scale"]} />;

// --- Gallery ---
const Gallery = () => {
  const images = Array(12).fill().map((_, i) => `https://placehold.co/400x300?text=Project+${i+1}`);
  return (
    <div className="max-w-7xl mx-auto px-4 py-12">
      <h1 className="text-4xl font-bold text-red-600 mb-8 text-center flex items-center justify-center">
        <Camera size={36} className="mr-3" /> Gallery
      </h1>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {images.map((src, i) => (
          <img key={i} src={src} alt="" className="w-full h-48 object-cover rounded-lg shadow hover:scale-105 transition" />
        ))}
      </div>
    </div>
  );
};

// --- Contact ---
const Contact = () => (
  <div className="max-w-7xl mx-auto px-4 py-12">
    <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">Contact Us</h1>
    <div className="grid md:grid-cols-2 gap-8">
      <div className="bg-white p-6 rounded-xl shadow">
        <h2 className="text-2xl font-bold mb-6">Get in Touch</h2>
        <div className="space-y-4 text-lg">
          <p className="flex items-center"><Mail size={20} className="mr-3 text-red-600" /> <a href={`mailto:${CONTACT_EMAIL}`} className="hover:text-red-600">{CONTACT_EMAIL}</a></p>
          <p className="flex items-center"><Phone size={20} className="mr-3 text-red-600" /> <a href={WHATSAPP_LINK} className="hover:text-red-600">{WHATSAPP_NUMBER}</a></p>
          <p className="flex items-start"><MapPin size={20} className="mr-3 mt-1 text-red-600" /> {COMPANY_ADDRESS}</p>
        </div>
      </div>
      <div className="h-96 rounded-xl overflow-hidden shadow-lg border-2 border-red-600">
        <iframe
          title="Senrans Location"
          src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3988.789!2d36.8219!3d-1.2921!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2zMcKwMTcnMzEuMiJTIDM2wrA0OScy0y4zIkU!5e0!3m2!1sen!2ske!4v1234567890"
          width="100%" height="100%" style={{ border: 0 }} allowFullScreen="" loading="lazy">
        </iframe>
      </div>
    </div>
  </div>
);

// --- Scroll to Top ---
const ScrollToTop = () => {
  const { pathname } = useLocation();
  React.useEffect(() => window.scrollTo(0, 0), [pathname]);
  return null;
};

// --- App ---
const App = () => (
  <Router>
    <ScrollToTop />
    <div className="min-h-screen bg-gray-50">
      <Header />
      <main>
        <Routes>
          <Route path="/" element={<HomeContent />} />
          <Route path="/about" element={<About />} />
          <Route path="/products-services" element={<Products />} />
          <Route path="/products-services/lv" element={<LV_Products />} />
          <Route path="/products-services/mvhv" element={<MVHV_Products />} />
          <Route path="/products-services/solar" element={<Solar_Products />} />
          <Route path="/gallery" element={<Gallery />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </main>
      <Footer />
      <a href={WHATSAPP_LINK} target="_blank" rel="noopener noreferrer"
         className="fixed bottom-6 right-6 bg-green-500 p-4 rounded-full text-white shadow-xl hover:scale-110 transition z-50">
        <Phone size={28} />
      </a>
    </div>
  </Router>
);

export default App;
