import React from 'react';
import { HashRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { Home, Menu, X, Mail, Phone, MapPin, Facebook, Linkedin, Instagram, ChevronDown, CheckCircle, Camera, Sun } from 'lucide-react';

// --- TikTok Icon ---
const TikTokIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
    <path d="M12.53 2.12c-.52.28-.79.88-.66 1.48.13.59.7.97 1.3.84 3.23-.7 6.64 1.15 7.37 4.79.4 1.94-.13 3.86-1.55 5.23-1.43 1.38-3.4 2.05-5.45 1.76l-.16.03c-.63.12-1.28.02-1.89-.3.51.13 1.05.18 1.57.14 2.05-.14 3.96-.86 5.39-2.23 1.42-1.38 1.95-3.3 1.55-5.23-.72-3.64-4.14-5.49-7.37-4.79ZM5.5 13.5v5c0 1.93 1.57 3.5 3.5 3.5h7c1.93 0 3.5-1.57 3.5-3.5v-5c0-.55-.45-1-1-1s-1 .45-1 1v5c0 .83-.67 1.5-1.5 1.5h-7c-.83 0-1.5-.67-1.5-1.5v-5c0-.55-.45-1-1-1s-1 .45-1 1ZM10 13c.55 0 1-.45 1-1v-4c0-.55-.45-1-1-1s-1 .45-1 1v4c0 .55.45 1 1 1ZM15 13c.55 0 1-.45 1-1v-4c0-.55-.45-1-1-1s-1 .45-1 1v4c0 .55.45 1 1 1Z"/>
  </svg>
);

// --- Config ---
const CONTACT_EMAIL = 'sales@senrans.com';
const WHATSAPP_NUMBER = '+254725723091';
const WHATSAPP_LINK = `https://wa.me/${WHATSAPP_NUMBER.replace(/\+/g, '')}`;
const COMPANY_ADDRESS = '1°22\'41.8"S 36°55\'43.4"E, Nairobi, Kenya';
const TIKTOK_LINK = 'https://www.tiktok.com/@senrans_ltd?_t=8qBVhEniFq1&_r=1';
const MAP_EMBED = `https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3988.789!2d36.928722!3d-1.378278!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2zMcKwMjInNDEuOCJTIDM2wrA1NSc0My40IkU!5e0!3m2!1sen!2ske!4v1234567890`;

// --- Nav Item ---
const NavItem = ({ to, children, isDropdown = false, onToggle }) => {
  const location = useLocation();
  const isActive = location.pathname === to || (to !== '/' && location.pathname.startsWith(to));
  return (
    <li className="relative group">
      <Link
        to={to}
        onClick={isDropdown ? onToggle : undefined}
        className={`flex items-center p-2 text-white font-semibold hover:text-red-200 transition ${isActive ? 'border-b-2 border-white' : ''}`}
      >
        {children}
        {isDropdown && <ChevronDown size={14} className="ml-1 transition-transform group-hover:rotate-180" />}
      </Link>
    </li>
  );
};

// --- Header (Red Background) ---
const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);
  const [isProductsOpen, setIsProductsOpen] = React.useState(false);
  const closeMenu = () => { setIsMenuOpen(false); setIsProductsOpen(false); };

  return (
    <header className="bg-red-600 shadow-lg sticky top-0 z-50 text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <Link to="/" className="flex items-center">
            <img src="/logo.png" alt="Senrans Ltd" className="h-10 w-10 rounded-full mr-2" />
            <span className="text-white text-xl font-extrabold tracking-wider">SENRANS LTD</span>
          </Link>

          {/* Desktop */}
          <nav className="hidden md:block">
            <ul className="flex space-x-4">
              <NavItem to="/">Home</NavItem>
              <NavItem to="/about">About Us</NavItem>
              <li className="relative group" onMouseLeave={() => setIsProductsOpen(false)}>
                <a href="#" onMouseEnter={() => setIsProductsOpen(true)} onClick={e => e.preventDefault()}
                   className="flex items-center p-2 text-white font-semibold hover:text-red-200">
                  Products & Solutions <ChevronDown size={14} className="ml-1 transition-transform group-hover:rotate-180" />
                </a>
                {isProductsOpen && (
                  <div className="absolute left-0 mt-0 w-48 bg-red-700 rounded-md shadow-lg py-1 z-50">
                    <Link to="/products-services" onClick={closeMenu} className="block px-4 py-2 text-sm text-white hover:bg-red-600">Overview</Link>
                    <Link to="/products-services/lv" onClick={closeMenu} className="block px-4 py-2 text-sm text-white hover:bg-red-600">Low Voltage</Link>
                    <Link to="/products-services/mvhv" onClick={closeMenu} className="block px-4 py-2 text-sm text-white hover:bg-red-600">MV & HV</Link>
                    <Link to="/products-services/solar" onClick={closeMenu} className="block px-4 py-2 text-sm text-white hover:bg-red-600">Solar PV</Link>
                  </div>
                )}
              </li>
              <NavItem to="/gallery">Gallery</NavItem>
              <NavItem to="/contact">Contact Us</NavItem>
            </ul>
          </nav>

          {/* Mobile */}
          <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="md:hidden text-white">
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="md:hidden bg-red-700">
          <div className="px-2 pt-2 pb-3 space-y-1">
            <Link to="/" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-600">Home</Link>
            <Link to="/about" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-600">About Us</Link>
            <button onClick={() => setIsProductsOpen(!isProductsOpen)} className="w-full text-left flex justify-between px-3 py-2 text-white hover:bg-red-600">
              Products & Solutions <ChevronDown size={16} className={isProductsOpen ? 'rotate-180' : ''} />
            </button>
            {isProductsOpen && (
              <div className="pl-6 space-y-1">
                <Link to="/products-services" onClick={closeMenu} className="block px-3 py-2 text-sm text-white hover:bg-red-500">Overview</Link>
                <Link to="/products-services/lv" onClick={closeMenu} className="block px-3 py-2 text-sm text-white hover:bg-red-500">Low Voltage</Link>
                <Link to="/products-services/mvhv" onClick={closeMenu} className="block px-3 py-2 text-sm text-white hover:bg-red-500">MV & HV</Link>
                <Link to="/products-services/solar" onClick={closeMenu} className="block px-3 py-2 text-sm text-white hover:bg-red-500">Solar PV</Link>
              </div>
            )}
            <Link to="/gallery" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-600">Gallery</Link>
            <Link to="/contact" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-600">Contact Us</Link>
          </div>
        </div>
      )}
    </header>
  );
};

// --- Footer (Red Background) ---
const Footer = () => (
  <footer className="bg-red-600 text-white mt-12">
    <div className="max-w-7xl mx-auto px-4 py-10">
      <div className="grid grid-cols-1 md:grid-cols-4 gap-8 border-b border-red-700 pb-8">
        <div>
          <div className="flex items-center mb-3">
            <img src="/logo.png" alt="Senrans Ltd" className="h-12 w-12 rounded-full mr-3" />
            <h3 className="text-xl font-bold">Senrans Ltd</h3>
          </div>
          <p className="text-sm text-red-100">
            Leading electrical engineering firm in Kenya, delivering MV/HV, LV, and Solar PV solutions with safety, innovation, and reliability.
          </p>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-white pb-1 text-lg">Quick Links</h3>
          <ul className="text-sm space-y-1">
            <li><Link to="/" className="hover:text-red-200 transition">Home</Link></li>
            <li><Link to="/about" className="hover:text-red-200 transition">About Us</Link></li>
            <li><Link to="/products-services" className="hover:text-red-200 transition">All Services</Link></li>
            <li><Link to="/gallery" className="hover:text-red-200 transition">Gallery</Link></li>
            <li><Link to="/contact" className="hover:text-red-200 transition">Contact</Link></li>
          </ul>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-white pb-1 text-lg">Our Services</h3>
          <ul className="text-sm space-y-1">
            <li><Link to="/products-services/lv" className="hover:text-red-200 transition">Low Voltage (LV)</Link></li>
            <li><Link to="/products-services/mvhv" className="hover:text-red-200 transition">MV & HV Systems</Link></li>
            <li><Link to="/products-services/solar" className="hover:text-red-200 transition">Solar PV Solutions</Link></li>
          </ul>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-white pb-1 text-lg">Contact Info</h3>
          <p className="flex items-start text-sm mb-2 text-red-100"><MapPin size={16} className="mr-2 mt-0.5" /> {COMPANY_ADDRESS}</p>
          <p className="flex items-center text-sm mb-2"><Mail size={16} className="mr-2" /> <a href={`mailto:${CONTACT_EMAIL}`} className="hover:text-red-200">{CONTACT_EMAIL}</a></p>
          <p className="flex items-center text-sm"><Phone size={16} className="mr-2" /> <a href={WHATSAPP_LINK} className="hover:text-red-200">{WHATSAPP_NUMBER}</a></p>
          <div className="flex space-x-3 mt-4">
            <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" className="text-red-200 hover:text-white"><Facebook size={20} /></a>
            <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer" className="text-red-200 hover:text-white"><Linkedin size={20} /></a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" className="text-red-200 hover:text-white"><Instagram size={20} /></a>
            <a href={TIKTOK_LINK} target="_blank" rel="noopener noreferrer" className="text-red-200 hover:text-white"><TikTokIcon /></a>
          </div>
        </div>
      </div>
      <p className="text-center text-sm text-red-200 pt-6">© {new Date().getFullYear()} Senrans Ltd. All rights reserved.</p>
    </div>
  </footer>
);

// --- Home ---
const HomeContent = () => {
  const [current, setCurrent] = React.useState(0);
  const slides = [
    { img: "/images/slide1.jpg", title: "33kV Substation Excellence", text: "Design, installation & commissioning" },
    { img: "/images/slide2.jpg", title: "Solar PV Power Plants", text: "Grid-tied & off-grid solutions" },
    { img: "/images/slide3.jpg", title: "Smart LV Control Panels", text: "Automation & energy efficiency" },
  ];

  React.useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  const partners = ['Siemens', 'Schneider', 'ABB', 'Eaton', 'Danfoss', 'Caterpillar'];

  return (
    <div>
      <div className="relative h-96 md:h-screen overflow-hidden">
        {slides.map((s, i) => (
          <div key={i} className={`absolute inset-0 transition-opacity duration-1000 ${i === current ? 'opacity-100' : 'opacity-0'}`}>
            <img src={s.img} alt={s.title} className="w-full h-full object-cover" />
            <div className="absolute inset-0 bg-black bg-opacity-60 flex items-center justify-center text-center p-6">
              <div>
                <h1 className="text-3xl md:text-6xl font-bold text-white mb-4">{s.title}</h1>
                <p className="text-lg md:text-2xl text-red-300">{s.text}</p>
              </div>
            </div>
          </div>
        ))}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
          {slides.map((_, i) => (
            <button key={i} onClick={() => setCurrent(i)} className={`w-3 h-3 rounded-full transition ${i === current ? 'bg-white' : 'bg-gray-500'}`} />
          ))}
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-12">
        <section className="bg-white p-8 rounded-xl shadow mb-12">
          <h2 className="text-3xl font-bold text-center mb-8">Trusted Partners</h2>
          <div className="grid grid-cols-2 md:grid-cols-6 gap-6">
            {partners.map(p => (
              <div key={p} className="bg-gray-100 h-20 rounded-lg flex items-center justify-center text-sm font-semibold text-gray-700">
                {p}
              </div>
            ))}
          </div>
        </section>

        <section className="bg-red-600 text-white p-10 rounded-xl text-center">
          <h3 className="text-2xl font-bold mb-4">Ready to Power Your Project?</h3>
          <a href={WHATSAPP_LINK} className="bg-white text-red-600 px-8 py-3 rounded-full font-bold inline-flex items-center hover:bg-gray-100">
            Get Quote <Phone size={20} className="ml-2" />
          </a>
        </section>
      </div>
    </div>
  );
};

// --- About Us ---
const About = () => {
  const collageImages = Array(6).fill().map((_, i) => `/images/collage${i + 1}.jpg`);

  return (
    <div className="max-w-7xl mx-auto px-4 py-12">
      <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">About Senrans Ltd</h1>

      <div className="bg-white p-10 rounded-xl shadow-lg text-lg leading-relaxed mb-12">
        <p className="mb-6">
          Senrans Ltd is a premier electrical engineering company based in Nairobi, Kenya, specializing in <strong>Low Voltage (LV)</strong>, <strong>Medium & High Voltage (MV/HV)</strong>, and <strong>Solar Photovoltaic (PV)</strong> systems. With over a decade of hands-on experience, we have established ourselves as trusted partners in delivering safe, efficient, and innovative power solutions across East Africa.
        </p>
        <p className="mb-6">
          From designing and commissioning 33kV substations to installing grid-tied solar farms and smart LV control panels, our team of certified engineers ensures every project meets international standards (IEC, KPLC, NEMA) while prioritizing sustainability and cost-efficiency. We work with global leaders like Siemens, Schneider Electric, and ABB to bring cutting-edge technology to local industries, commercial buildings, and utility-scale projects.
        </p>
        <p className="italic text-gray-700">
          "We don’t just supply power — we engineer progress."
        </p>
      </div>

      <h2 className="text-3xl font-bold text-red-600 mb-6 text-center">Our Work in Action</h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {collageImages.map((src, i) => (
          <div key={i} className="relative group overflow-hidden rounded-lg shadow-lg">
            <img src={src} alt={`Project ${i+1}`} className="w-full h-64 object-cover transition-transform group-hover:scale-110" />
            <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-50 transition flex items-center justify-center">
              <p className="text-white font-bold opacity-0 group-hover:opacity-100 transition">Project {i+1}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// --- Products & Solutions ---
const Products = () => {
  const services = [
    { title: "Low Voltage (LV)", img: "/images/lv-panel.jpg", desc: "Distribution panels, MCCs, lighting, automation", to: "/products-services/lv" },
    { title: "MV & HV Systems", img: "/images/substation.jpg", desc: "33kV substations, switchgear, cable jointing", to: "/products-services/mvhv" },
    { title: "Solar PV Solutions", img: "/images/solar-farm.jpg", desc: "Rooftop, battery storage, hybrid, utility-scale", to: "/products-services/solar" },
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 py-12">
      <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">Products & Solutions</h1>
      <div className="grid md:grid-cols-3 gap-8">
        {services.map((s, i) => (
          <Link to={s.to} key={i} className="group bg-white rounded-xl shadow hover:shadow-2xl transition overflow-hidden">
            <img src={s.img} alt={s.title} className="w-full h-48 object-cover group-hover:scale-105 transition" />
            <div className="p-6">
              <h3 className="text-xl font-bold text-red-600 mb-2">{s.title}</h3>
              <p className="text-gray-600">{s.desc}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

const ProductDetail = ({ title, items, img }) => (
  <div className="max-w-7xl mx-auto px-4 py-12">
    <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">{title}</h1>
    <img src={img} alt={title} className="w-full h-96 object-cover rounded-xl shadow-lg mb-8" />
    <ul className="space-y-4">
      {items.map((item, i) => (
