import React from 'react';
import { HashRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { Menu, X, Mail, Phone, MapPin, Facebook, Linkedin, Instagram, Camera, Clock, ArrowRight, Zap, Wrench, Settings } from 'lucide-react';

// --- TikTok SVG ---
const TikTokIcon = () => (
  <svg width="20" height="20" viewBox="0 0 256 256" fill="currentColor">
    <path d="M164.4 24a44.9 44.9 0 0 0 25.7 8.2V67a78.9 78.9 0 0 1-26.4-4.8v66.6a65.4 65.4 0 1 1-65.4-65.4h.7v33.1a32.3 32.3 0 1 0 22.3 30.6V24h43.1Z"/>
  </svg>
);

// --- Config ---
const CONTACT_EMAIL = 'sales@senrans.com';
const WHATSAPP_LINK = 'https://wa.me/254757689205';
const COMPANY_ADDRESS = 'Repen Complex, Block B, 3rd Floor, office 310B, Syokimau, off Mombasa Rd, Nairobi, Kenya';
const TIKTOK_LINK = 'https://www.tiktok.com/@senrans_ltd';

const SOCIAL_LINKS = {
  facebook: 'https://www.facebook.com/people/Senrans-Ventures/pfbid0XufECfqUEoAKRqbuHT2vkn8EdSvHQxFX8HueCJYLHYb2ajG19v8ntsadvQpXn9nl/',
  instagram: 'https://www.instagram.com/senrans_ltd_/?igsh=MTk5OWJ2NnRudWoxag%3D%3D',
  linkedin: 'https://www.linkedin.com/company/senrans-ventures-ltd/',
  tiktok: TIKTOK_LINK
};

// --- Nav Item ---
const NavItem = ({ to, children }) => {
  const location = useLocation();
  const isActive = location.pathname === to || (to !== '/' && location.pathname.startsWith(to));
  return (
    <li>
      <Link to={to} className={`block px-3 py-2 text-white font-semibold hover:text-red-400 transition ${isActive ? 'border-b-2 border-white' : ''}`}>
        {children}
      </Link>
    </li>
  );
};

// --- Header ---
const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);
  const closeMenu = () => setIsMenuOpen(false);

  return (
    <header className="bg-black shadow-lg sticky top-0 z-50 text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <Link to="/" className="flex items-center">
            <img src="/logo.png" alt="Senrans Ltd" className="h-12 w-auto" />
          </Link>
          <nav className="hidden md:block">
            <ul className="flex space-x-6">
              <NavItem to="/about">About Us</NavItem>
              <NavItem to="/products-services">Products & Services</NavItem>
              <NavItem to="/gallery">Gallery</NavItem>
              <NavItem to="/contact">Contact Us</NavItem>
            </ul>
          </nav>
          <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="md:hidden text-white">
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </div>
      {isMenuOpen && (
        <div className="md:hidden bg-red-800">
          <div className="px-2 pt-2 pb-3 space-y-1">
            <Link to="/about" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">About Us</Link>
            <Link to="/products-services" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Products & Services</Link>
            <Link to="/gallery" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Gallery</Link>
            <Link to="/contact" onClick={closeMenu} className="block px-3 py-2 text-white hover:bg-red-700">Contact Us</Link>
          </div>
        </div>
      )}
    </header>
  );
};

// --- PARTNERS CAROUSEL (EATON REMOVED) ---
const PartnersCarousel = () => {
  const partners = [
    { name: "Siemens", logo: "/partners/siemens.png" },
    { name: "Schneider", logo: "/partners/schneider.png" },
    { name: "ABB", logo: "/partners/abb.png" },
    { name: "Danfoss", logo: "/partners/danfoss.png" },
    { name: "Deep Sea Electronics", logo: "/partners/deepsea.png" },
    { name: "Himel", logo: "/partners/himel.png" },
  ];
  const doubled = [...partners, ...partners];

  return (
    <section className="bg-white py-12 overflow-hidden">
      <div className="max-w-7xl mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-10 text-gray-800">PARTNERS WE WORK WITH</h2>
        <div className="flex animate-infinite-scroll gap-8">
          {doubled.map((p, i) => (
            <div key={i} className="flex-shrink-0 w-32 h-20 md:w-40 md:h-24 bg-gray-50 rounded-lg flex items-center justify-center p-4 shadow-sm">
              <img src={p.logo} alt={p.name} className="max-h-full max-w-full object-contain" />
            </div>
          ))}
        </div>
      </div>
      <style jsx>{`
        @keyframes infiniteScroll { 0% { transform: translateX(0); } 100% { transform: translateX(-50%); } }
        .animate-infinite-scroll { display: flex; width: max-content; animation: infiniteScroll 18s linear infinite; }
      `}</style>
    </section>
  );
};

// --- HOME PAGE ---
const HomeContent = () => {
  const [current, setCurrent] = React.useState(0);
  const slides = [
    { img: "/images/lv-panel-1.jpg", title: "Custom-made LV Panels", text: "Built to your exact specs" },
    { img: "/images/dse8610.jpg", title: "DSE8610 Controller", text: "Seamless auto-transfer" },
    { img: "/images/smart-mcc.jpg", title: "Smart MCCs", text: "Automation & control" },
  ];

  React.useEffect(() => {
    const interval = setInterval(() => setCurrent((prev) => (prev + 1) % slides.length), 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <link href="https://fonts.googleapis.com/css2?family=Alegreya:wght@900&display=swap" rel="stylesheet" />
      <style jsx>{`.alegreya-hero { font-family: 'Alegreya', serif; font-weight: 900; text-shadow: 0 0 15px rgba(0,0,0,0.7); }`}</style>

      {/* HERO SLIDER */}
      <div className="relative h-96 md:h-screen overflow-hidden">
        {slides.map((s, i) => (
          <div key={i} className={`absolute inset-0 transition-opacity duration-1000 ${i === current ? 'opacity-100' : 'opacity-0'}`}>
            <img src={s.img} alt={s.title} className="w-full h-full object-cover brightness-75" />
            <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent flex items-center justify-center text-center p-6">
              <div className="max-w-4xl">
                <h1 className="alegreya-hero text-4xl md:text-7xl text-white mb-4">{s.title}</h1>
                <p className="text-xl md:text-3xl text-yellow-300">{s.text}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="max-w-7xl mx-auto px-4 py-12">
        <PartnersCarousel />

        <section className="bg-red-600 text-white p-10 rounded-xl text-center my-12">
          <h3 className="text-2xl font-bold mb-4">Need LV Panels or Electrical Service?</h3>
          <a href={WHATSAPP_LINK} className="bg-white text-red-600 px-8 py-3 rounded-full font-bold inline-flex items-center hover:scale-105 transition">
            Get Quote <Phone size={20} className="ml-2" />
          </a>
        </section>

        {/* ELECTRICAL INSTALLATIONS */}
        <div className="grid md:grid-cols-2 gap-6 mb-16">
          <img src="/images/installation1.jpg" alt="Electrical Installations" className="rounded-lg shadow-lg w-full h-64 object-cover" />
          <img src="/images/installation2.jpg" alt="Electrical Installations" className="rounded-lg shadow-lg w-full h-64 object-cover" />
        </div>

        {/* SERVICE & MAINTENANCE */}
        <div className="grid md:grid-cols-2 gap-6">
          <img src="/images/maintenance1.jpg" alt="Service & Maintenance" className="rounded-lg shadow-lg w-full h-64 object-cover" />
          <img src="/images/maintenance2.jpg" alt="Service & Maintenance" className="rounded-lg shadow-lg w-full h-64 object-cover" />
        </div>
      </div>
    </div>
  );
};

// --- ABOUT US ---
const About = () => {
  return (
    <div className="max-w-7xl mx-auto px-4 py-12">
      <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">About Senrans Ventures Ltd</h1>
      <div className="bg-white p-8 rounded-xl shadow-lg text-lg leading-relaxed mb-12">
        <p className="mb-6"><strong>Senrans Ltd</strong> is an electrical engineering company that offers Low Voltage & Medium Voltage solutions from design, supply, installation, commissioning and after sales support. We are also at the fore front to promote clean energy & power backup solutions.</p>

        <p className="mb-6">Our electrical services cover predictive, preventative & corrective maintenance 24hrs 7days a week. We do general inspection for any physical damage, operational check, earth connection, fault diagnosis, trouble shooting, thermography services and safe cleaning of switchgears.</p>

        <p className="mb-6">Senrans assemble LV panels that are custom made for all applications. Our range of panels include:</p>
        <ul className="list-disc list-inside space-y-2 mb-6 ml-4">
          <li>Changeover panels</li>
          <li>Synch & Control panels</li>
          <li>Metering panels</li>
          <li>Distribution boards</li>
          <li>Power factor correction banks</li>
        </ul>

        <div className="grid md:grid-cols-2 gap-8 mt-10">
          <div className="bg-gray-50 p-6 rounded-lg"><h3 className="text-xl font-bold text-red-600 mb-3">OUR VISION</h3><p>To be the trusted service provider in Engineering solutions in East Africa</p></div>
          <div className="bg-gray-50 p-6 rounded-lg"><h3 className="text-xl font-bold text-red-600 mb-3">Our Mission</h3><p>To provide high quality electrical solutions through customized designs that promote energy efficiency & sustainability in all sectors.</p></div>
        </div>
      </div>

      <h2 className="text-3xl font-bold text-red-600 mb-6 text-center">Projects</h2>
      <div className="grid md:grid-cols-2 gap-6">
        <img src="/images/project-person1.jpg" alt="Team at Work" className="rounded-lg shadow-lg w-full h-80 object-cover" />
        <img src="/images/project-person2.jpg" alt="Team at Work" className="rounded-lg shadow-lg w-full h-80 object-cover" />
      </div>
    </div>
  );
};

// --- PRODUCTS & SERVICES ---
const ProductsList = () => (
  <div className="max-w-7xl mx-auto px-4 py-12">
    <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">LV & MV Products & Services</h1>
    <h2 className="text-2xl font-bold mb-6">What We Do</h2>
    <ul className="list-disc list-inside space-y-3 text-lg mb-12">
      <li>Electrical Installations</li>
      <li>Electrical services i.e</li>
      <li className="ml-6">Power factor correction</li>
      <li className="ml-6">Annual Maintenance Contracts (AMCs)</li>
      <li className="ml-6">Electrical inspections</li>
      <li>Thermography services</li>
      <li>Assembly & installation of LV panels</li>
      <li>Supply of LV & MV electrical switchgears</li>
      <li>Supply of DSE generator controllers</li>
      <li>Backup solutions</li>
    </ul>

    <div className="grid md:grid-cols-2 gap-8">
      <div><h3 className="text-xl font-bold text-red-600 mb-3">MV Solutions</h3><img src="/images/mv-solutions.jpg" alt="MV" className="rounded-lg shadow-lg w-full h-64 object-cover" /></div>
      <div><h3 className="text-xl font-bold text-red-600 mb-3">LV Panels</h3><img src="/images/lv-panels.jpg" alt="LV" className="rounded-lg shadow-lg w-full h-64 object-cover" /></div>
      <div><h3 className="text-xl font-bold text-red-600 mb-3">DSE Generator Controllers</h3><img src="/images/dse-controllers.jpg" alt="DSE" className="rounded-lg shadow-lg w-full h-64 object-cover" /></div>
      <div><h3 className="text-xl font-bold text-red-600 mb-3">LV Circuit Breakers</h3><img src="/images/breakers.jpg" alt="Breakers" className="rounded-lg shadow-lg w-full h-64 object-cover" /></div>
    </div>
  </div>
);

// --- GALLERY (Small images + yellow camera on image 3) ---
const Gallery = () => {
  const images = Array.from({ length: 12 }, (_, i) => `/gallery/new${i + 1}.jpg`);
  return (
    <div className="max-w-7xl mx-auto px-4 py-12">
      <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">Gallery</h1>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {images.map((src, i) => (
          <div key={i} className="relative group">
            <img src={src} alt={`Project ${i+1}`} className="w-full h-32 md:h-40 object-cover rounded-lg shadow hover:scale-105 transition" />
            {i === 6 && <Camera className="absolute top-2 right-2 text-yellow-400 bg-black/60 p-1 rounded" size={20} />}
          </div>
        ))}
      </div>
    </div>
  );
};

// --- CONTACT ---
const Contact = () => (
  <div className="max-w-7xl mx-auto px-4 py-12">
    <h1 className="text-4xl font-bold text-red-600 mb-8 text-center">Contact Us</h1>
    <div className="grid md:grid-cols-2 gap-8">
      <div className="bg-white p-6 rounded-xl shadow">
        <h2 className="text-2xl font-bold mb-6">Get in Touch</h2>
        <div className="space-y-4 text-lg">
          <p className="flex items-center"><Mail size={20} className="mr-3 text-red-600" /> <a href={`mailto:${CONTACT_EMAIL}`} className="hover:text-red-600">{CONTACT_EMAIL}</a></p>
          <p className="flex items-center"><Phone size={20} className="mr-3 text-red-600" /> <a href="tel:+254757689205" className="hover:text-red-600">+254 757 689 205</a></p>
          <p className="flex items-center"><Phone size={20} className="mr-3 text-red-600" /> <a href="tel:+254755388676" className="hover:text-red-600">+254 755 388 676</a></p>
          <p className="flex items-start"><MapPin size={20} className="mr-3 mt-1 text-red-600" /> {COMPANY_ADDRESS}</p>
          <p className="flex items-start"><Clock size={20} className="mr-3 mt-1 text-red-600" /> 
            MondayFriday: 8:00 AM  5:00 PM<br/>
            Saturdays: 8:00 AM  12:00 PM
          </p>
        </div>
      </div>
      <div className="h-96 rounded-xl overflow-hidden shadow-lg border-2 border-red-600">
        <iframe title="Location" src="https://www.google.com/maps/embed?pb=..." width="100%" height="100%" style={{ border: 0 }} allowFullScreen="" loading="lazy"></iframe>
      </div>
    </div>
  </div>
);

// --- FOOTER ---
const Footer = () => (
  <footer className="bg-black text-white mt-12">
    <div className="max-w-7xl mx-auto px-4 py-10">
      <div className="grid grid-cols-1 md:grid-cols-4 gap-8 border-b border-gray-800 pb-8">
        <div>
          <Link to="/" className="flex items-center mb-3"><img src="/logo.png" alt="Senrans Ltd" className="h-12 w-auto" /></Link>
          <p className="text-sm text-gray-400"><strong>Senrans Ventures Ltd</strong>  LV panels expert, Electrical services, DSE generator controllers, LV & MV products.</p>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-red-600 pb-1 text-lg">Quick Links</h3>
          <ul className="text-sm space-y-1">
            <li><Link to="/" className="hover:text-red-400">Home</Link></li>
            <li><Link to="/about" className="hover:text-red-400">About</Link></li>
            <li><Link to="/products-services" className="hover:text-red-400">Products</Link></li>
            <li><Link to="/gallery" className="hover:text-red-400">Gallery</Link></li>
            <li><Link to="/contact" className="hover:text-red-400">Contact</Link></li>
          </ul>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-red-600 pb-1 text-lg">Products</h3>
          <ul className="text-sm space-y-1">
            <li><Link to="/products-services" className="hover:text-red-400">MV Solutions</Link></li>
            <li><Link to="/products-services" className="hover:text-red-400">LV Panels</Link></li>
            <li><Link to="/products-services" className="hover:text-red-400">DSE Controllers</Link></li>
            <li><Link to="/products-services" className="hover:text-red-400">LV Breakers</Link></li>
          </ul>
        </div>
        <div>
          <h3 className="font-bold mb-3 border-b border-red-600 pb-1 text-lg">Contact</h3>
          <p className="text-sm mb-2"><MapPin size={16} className="inline mr-1" /> {COMPANY_ADDRESS}</p>
